package com.nanos.dsa.string;

public class NumberofSubstringsWithOnly1 {
    public static void main(String[] args) {
        String s ="";
        System.out.println(numSub(s));
    }
    public static int numSub(String s) {
        int res=0;
        int mod = 1_000_000_007;
        int st=0,cnt=0;
        while(st<s.length()){
            if(st<s.length()&&s.charAt(st)=='1'){
                cnt++;
                res=((res)+cnt)%mod;
            }else{
                cnt=0;
            }
            st++;
        }
        return res;
    }

}
