package com.AlgorithmI;

public class PermutationinString {
    public static void main(String[] args) {
        String s1="";
        String s2="";
        System.out.println(checkInclusion(s1,s2));
    }
    public static boolean checkInclusion(String s1, String s2) {
        int s=0,e=s1.length();
        if(s1.length()>s2.length())
            return false;
        int[] frq1=new int[26];
        for (int i = 0; i < s1.length(); i++) {
           ++frq1[(int) (s1.charAt(i) - 'a')];
        }
        int[] frq2=new int[26];
        for (int i = 0; i < s1.length(); i++) {
            ++frq2[(int) (s2.charAt(i) - 'a')];
        }
        if(isSim(frq1,frq2))
            return true;
        while(s<=e&&e<s2.length()){
            char rem =s2.charAt(s);
            char add=s2.charAt(e);
            --frq2[(int) (rem - 'a')];
            ++frq2[(int) (add- 'a')];
            if (isSim(frq1,frq2))
                return true;
            s++;
            e++;
        }
        return false;
    }
    public static boolean isSim(int[] frq1,int[] frq2){
        for (int i = 0; i < frq1.length; i++) {
            if(frq1[i]>0){
                if(frq1[i]!=frq2[i])
                    return false;
            }
        }
        return true;
    }
}
